import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import ValidationError from "../../common/FormError";
import useAxios from "../../hooks/useAxios";
import Heading from "../layout/Heading";
import { COMMENTS_PATH } from "../../../constants/api";


const schema = yup.object().shape({
	title: yup.string().required("Title is required"),
});

export default function ContactForm() {
	const [submitting, setSubmitting] = useState(false);
	const [serverError, setServerError] = useState(null);

	const history = useNavigate();
	const http = useAxios();

	const { register, handleSubmit } = useForm({
		resolver: yupResolver(schema),
	});

	async function onSubmit(data) {
		setSubmitting(true);
		setServerError(null);

		data.status = "publish";

		if (data.featured_media === "") {
			data.featured_media = null;
		}

		console.log(data);

		try {
			const response = await http.post(COMMENTS_PATH, data);
			console.log("response", response.data);
			history("/admin");
		} catch (error) {
			console.log("error", error);
			setServerError(error.toString());
		} finally {
			setSubmitting(false);
		}
	}

	return (

			<form onSubmit={handleSubmit(onSubmit)}>
				{serverError && <ValidationError>{serverError}</ValidationError>}
				<fieldset disabled={submitting}>
					
                    
                    <div>
						<p>Navn</p>
						<input name="name" type="text" {...register("author_name", {required: true})}/>
					</div>
                    <div>
                        <div>
                            <p>E-post</p>
                            <input name="name" type="email" {...register("author_email", {required: true})}/>
                        </div>

                        <div>
                            <p>Telefonnummer</p>
                            <input name="name" type="text" {...register("author", {required: true})}/>
                        </div>
                    </div>

                    {/* <div>
						<p>Emne</p>
						<input name="name" type="text" {...register("author", {required: true})}/>
					</div> */}

					<div>
						<p>Melding</p>
						<textarea  rows={5} name="content" type="text" {...register("content", {required: true})} />
					</div>


					<hr/>

					<button className="add-btn">{submitting ? "Submitting..." : "Submit"}</button>
				</fieldset>
			</form>

	)}
