import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import ValidationError from "../../common/FormError";
import useAxios from "../../../hooks/useAxios";
import Heading from "../../layout/Heading";
import { POSTS_PATH } from "../../../constants/api";
import AddImage from "./AddImage";

const schema = yup.object().shape({
	title: yup.string().required("Title is required"),
});

export default function AddPost() {
	const [submitting, setSubmitting] = useState(false);
	const [serverError, setServerError] = useState(null);

	const history = useNavigate();
	const http = useAxios();

	const { register, handleSubmit } = useForm({
		resolver: yupResolver(schema),
	});

	async function onSubmit(data) {
		setSubmitting(true);
		setServerError(null);

		data.status = "publish";

		if (data.featured_media === "") {
			data.featured_media = null;
		}

		console.log(data);

		try {
			const response = await http.post(POSTS_PATH, data);
			console.log("response", response.data);
			history("/admin");
		} catch (error) {
			console.log("error", error);
			setServerError(error.toString());
		} finally {
			setSubmitting(false);
		}
	}

	return (
		<div className="add-container">
		<Heading content="Add post" />
			<form onSubmit={handleSubmit(onSubmit)}>
				{serverError && <ValidationError>{serverError}</ValidationError>}
				<fieldset disabled={submitting}>
					<div>
						<p>Tittel</p>
						<input name="title" type="text" {...register("title", {required: true})}/>
					</div>

					<div>
						<p>Hoved Bilde</p>
						<input name="x_featured-media" type="file" {...register("x_featured-media", {required: true})}/>
					</div>

					<div>
						<p>Innhold</p>
						<textarea  rows={5} name="content" type="text" {...register("content", {required: true})} />
					</div>

					<AddImage/>

					<button className="add-btn">{submitting ? "Submitting..." : "Submit"}</button>
				</fieldset>
			</form>
		</div>
	)}
