import Heading from "../layout/Heading";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { BASE_URL, PAGES_PATH} from "../../constants/api"

function Home() {
	const [pages, setPages] = useState([]);
	const [loading, setLoading] = useState(true);
	const [error, setError] = useState(null);
    const API = BASE_URL + PAGES_PATH

	useEffect(function () {
		async function getPages() {
			try {
				const response = await fetch(API);

				if (response.ok) {
					const json = await response.json();
					setPages(json);
                    console.log(json)
				} else {
					setError("An error occured");
				}
			} catch (error) {
				setError(error.toString());
			} finally {
				setLoading(false);
			}
		}

		getPages();
         // eslint-disable-next-line react-hooks/exhaustive-deps
	}, []);

	if (loading) {
        return <div>Loading pages...</div>;
    }
	if (error) {
        return <div>An error occured: {error}</div>;
    }
	return (
		<>
      <Heading content="Tjenster" />
      <div className="pages">
        {pages.map((page) => {
          return (
            <div className="card" style="width: 18rem;">
              <img src="${imgUrl}" class="card-img-top" alt="${results[i].description}"/>
                <div class="card-body">
                    <h2 class="card-title">${page.title.renderd}</h2>
                    <Link to={`/detail/${page.id}`}/>
                </div>
            </div>
          );
        })};
      </div>
		</>
	);
}

export default Home
