import ReferencesCall from "./ReferencesCall";

export function searchReference(reference) {
    const search = document.querySelector(".search");

    search.onkeyup = function (event) {
        const searchValue = event.target.value.trim().toLowerCase();

        const filteredReference = reference.filter(function (reference) {
            if (reference.title.toLowerCase().includes(searchValue)) {
                return true;
            }
            else {
                return false;
            }
        });
        ReferencesCall(filteredReference)
    };
}
