						<Link to={`/detail/${band.id}`}>
                            <h2>{band.title.rendered}</h2>
                            <img src={band.x_featured_media_medium} alt={band.title.renderd} />
                        </Link>
					</li>
				);
			})}
		</ul>
		</>
	);
}

export default Home
