	const [loading, setLoading] = useState(true);
	const [error, setError] = useState(null);
  	const API = BASE_URL + COMMENTS_PATH;

	useEffect(function () {
		async function getComments() {
			try {
				const response = await fetch(API);
				console.log(response)

