import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import ValidationError from "../common/FormError";
import useAxios from "../../hooks/useAxios";
import { COMMENTS_PATH } from "../../constants/api";


const schema = yup.object().shape({
	
});

export default function ContactForm() {
	const [submitting, setSubmitting] = useState(false);
	const [serverError, setServerError] = useState(null);

	const history = useNavigate();
	const http = useAxios();

	const { register, handleSubmit } = useForm({
		resolver: yupResolver(schema),
	});

	async function onSubmit(data) {
		setSubmitting(true);
		setServerError(null);

		data.status = "publish";

		if (data.featured_media === "") {
			data.featured_media = null;
		}

		console.log(data);

		try {
			const response = await http.post(COMMENTS_PATH, data);
			console.log("response", response.data);
			history("/");
		} catch (error) {
			console.log("error", error);
			setServerError(error.toString());
		} finally {
			setSubmitting(false);
		}
	}

	return (
							
                	<form onSubmit={handleSubmit(onSubmit)}>
      				<input type="text" placeholder="Name" {...register("author_name", {})} />
					<input type="email" placeholder="Email" {...register("author_email", {})} />
					<input type="number" placeholder="Phone Number" {...register("author", {})} />
					<input type="text" placeholder="Subject" {...register("author_", {})} />
					<input type="text" placeholder="Message" {...register("author_content", {})} />
					<hr/>
      				<input type="submit" />
    			</form>

	)}
