import Heading from "../layout/Heading";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { BASE_URL, PAGES_PATH } from "../../constants/api";

function ReferenceDetails() {
    const [referance, setReferance] = useState(null);
    const [loading, setLoading] = useState(true);
	const [error, setError] = useState(null);
    
	let { id } = useParams();

    const newUrl =  BASE_URL + POSTS_PATH + id;

    useEffect(function () {
		async function getReferance() {
			try {
				const response = await fetch(newUrl);

				if (response.ok) {
					const json = await response.json();
					setReferance(json);
                    console.log("Info", json)
				} else {
					setError("An error occured");
				}
			} catch (error) {
				setError(error.toString());
			} finally {
				setLoading(false);
			}
		}

		getReferance();
         // eslint-disable-next-line react-hooks/exhaustive-deps
	}, []);

    if (loading) {
        return <div>Loading page...</div>;
    }
	if (error) {
        return <div>An error occured: {error}</div>;
    }
	return (
            <>
            <Heading content={referance.title.rendered} />
			<div className="container">
				<p dangerouslySetInnerHTML={{__html:referance.excerpt.rendered }}></p>
                <img src={referance.x_featured_media_original} alt={referance.title.renderd} />
			</div>
            </>	
	);
}

export default ReferenceDetails
